import React, { Component } from 'react'
import './SignupStyle.css';
import axios from 'axios'
import { NavLink } from 'react-router-dom';

class SignUp extends Component {
	constructor(props) {
		super(props)

		this.state = {
			name: "",
			password: "",
			email: "",
			phone: ""
		}
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleSubmitLogin = this.handleSubmitLogin.bind(this);
	}

	signUpModal = (e) => {
		document.getElementById('container').classList.add("right-panel-active");
	}

	signInModal = (e) => {
		document.getElementById('container').classList.remove("right-panel-active");
	}

	handleChange(event) {
		// let target = event.target;
		// let value = target.type === "checkbox" ? target.checked : target.value;
		// let name = target.name;

		this.setState({
			[event.target.name]: event.target.value
		});
	}

	handleSubmit(e) {
		e.preventDefault();

		console.log("The form was submitted with the following data:");
		console.log(this.state);
		axios.post('http://localhost:8080/signup', this.state)
			.then(response => {
				alert("Registered!!!  Proceed to login now :)");
				console.log(response)
			})
			.catch(error => {
				alert("Incorrect Details");
				console.log(error)
			})

	}

	handleSubmitLogin(e) {
		e.preventDefault();

		console.log("The form was submitted with the following data:");
		console.log(this.state);
		axios.post('http://localhost:8080/login', this.state)
			.then(response => {
				console.log(response)
			})
			.catch(error => {
				alert("Incorrect Credentials");
				console.log(error)
			})
	}

	render() {
		return (
			<div className='page'>
				<nav className='primary-nav'>
					<NavLink to='/'>Home</NavLink>
					<NavLink to='/about'>About</NavLink>
					<NavLink to='/products'>Products</NavLink>
					<NavLink to='/profile'>Profile</NavLink>
				</nav>
				<div className="container" id="container">
					<div className="form-container sign-up-container">
						<form onSubmit={this.handleSubmit}>
							<h1>Create Account</h1>
							<input type="text" placeholder="Name"
								name="name"
								value={this.state.name}
								onChange={this.handleChange} required
							/>
							<input type="email" placeholder="Email"
								name="email"
								value={this.state.email}
								onChange={this.handleChange} required
							/>
							<input type="password" placeholder="Password"
								name="password"
								value={this.state.password}
								onChange={this.handleChange} required
							/>
							<input type="number" placeholder="Phone No."
								name="phone"
								value={this.state.phone}
								onChange={this.handleChange} required
							/>
							<button>Sign Up</button>
						</form>
					</div>
					<div className="form-container sign-in-container">
						<form onSubmit={this.handleSubmitLogin}>
							<h1>Sign in</h1>
							<input type="email" placeholder="Email"
								name="email"
								value={this.state.email}
								onChange={this.handleChange} />
							<input type="password"
								placeholder="Password"
								name="password"
								value={this.state.password}
								onChange={this.handleChange} />
							<a href="#">Forgot your password?</a>
							<button>Sign In</button>
						</form>
					</div>
					<div className="overlay-container">
						<div className="overlay">
							<div className="overlay-panel overlay-left">
								<h1>Welcome Back!</h1>
								<p>To keep connected with us please login with your personal info</p>
								<button className="ghost" id="signIn" onClick={this.signInModal}>Sign In</button>
							</div>
							<div className="overlay-panel overlay-right">
								<h1>Hello, Friend!</h1>
								<p>Enter your personal details and start journey with us</p>
								<button className="ghost" id="signUp" onClick={this.signUpModal}>Sign Up</button>
							</div>
						</div>
					</div>
				</div>
			</div>
		)
	}
}

export default SignUp
