import axios from 'axios'
import React, { Component } from 'react'
import CartItem from './CartItem'

export class Cart extends Component {
  constructor(props) {
    super(props)
    this.state = {
      cart: ""
    }
  }

  componentDidMount() {
    axios.get(`http://localhost:8080//cart/${localStorage.getItem("id")}/getCart`)
      .then(response => {
        console.log(response.data)
        this.setState({
          cart: response.data

        })
      })
      .catch(error => {
        console.log(error)
      })
      console.log(this.state.cart)
  }

  renderCartItems = () => {
    return this.state.cart.cartItem.map(cartItems => <CartItem key={cartItems.id} cartItems={cartItems} />);
  }

  render() {
    console.log(this.state.cart.cartItem)
    return (
      <div>
        {this.renderCartItems()}
      </div>
    )
  }
}

export default Cart
