import axios from 'axios';
import React from 'react'
import Products from './Products'

import { Component } from 'react'
class Home extends Component {

  constructor(props) {
    super(props)
    this.state = {
      products: []
    }
  }

  componentDidMount() {
    axios.get("http://localhost:8080/products/getAllProducts")
      .then(response => {
        this.setState({
          products: response.data
        })
      })
      .catch(error => {
        console.log(error)
      })
  }

  renderProducts = () => {
    return this.state.products.map(person => <Products key={person.id} person={person} />);
  }

  render() {
    return (

      <div>
        <div className='d-flex'>
          Filter: Minimum Price <input style={{height:"40px", width:"150px"}}/> Maximum Price <input style={{height:"40px", width:"150px"}}/> <button>Apply</button>
        </div>
        {this.renderProducts()}
      </div>
    )
  }
}
export default Home
