import axios from 'axios'
import React, { Component } from 'react'

export class Cart extends Component {
  constructor(props) {
    super(props)
    this.state = {
      cart: ""
    }
  }

  componentDidMount() {
    axios.get(`http://localhost:8080//cart/${localStorage.getItem("id")}/getCart`)
      .then(response => {
        this.setState({
          cart: response.data
        })
      })
      .catch(error => {
        console.log(error)
      })
  }

  renderProducts = () => {
    return this.state.cart.cartItem.map(person => <Products key={person.id} person={person} />);
  }

  render() {
    return (
      <div>
        {this.renderProducts()}
      </div>
    )
  }
}

export default Cart
