import axios from 'axios';
import React from 'react'
import Products from './Products'

import { Component } from 'react'
class Home extends Component {

  constructor(props) {
    super(props)
    this.state = {
      products: []
    }
  }

  componentDidMount() {
    axios.get("http://localhost:8080/products/getAllProducts")
      .then(response => {
        this.setState({
          products: response.data
        })
      })
      .catch(error => {
        console.log(error)
      })
  }

  renderProducts = () => {
    return this.state.products.map(person => <Products key={person.id} person={person} />);
  }

  render() {
    return (

      <div>
        <div className='d-flex m-5' style={{ textAlign: "center" }}>
          <b
            style={{ fontSize: "24px", display: "inline-block", marginTop: "8px", }}>
            Filter: </b>
          <span
            style={{ height: "40px", marginTop: "13px", marginLeft: "20px", marginRight: "22px", fontSize: "18px" }}>
            Minimum Price</span>
          <input
            style={{ height: "40px", width: "150px" }} />
          <span
            style={{ height: "40px", marginTop: "13px", marginLeft: "20px", marginRight: "22px", fontSize: "18px" }}>
            Maximum Price</span>
          <input style={{ height: "40px", width: "150px" }} /> <button>Apply</button>
        </div>
        {this.renderProducts()}
      </div>
    )
  }
}
export default Home
